let gulp=require("gulp"),sass=require("gulp-sass"),autoprefixer=require("gulp-autoprefixer"),concat=require("gulp-concat"),uglify=require("gulp-uglify-es").default,csso=require("gulp-csso"),rename=require("gulp-rename");var css={src:"css/**/*.scss",dest:"dist/css",filename:"theme.css"},js={src:"js/**/*.js",dest:"dist/js",filename:"theme.js"};function style(){return gulp.src(css.src).pipe(concat(css.filename)).pipe(sass()).on("error",sass.logError).pipe(autoprefixer()).pipe(gulp.dest(css.dest)).pipe(csso()).pipe(rename({extname:".min.css"})).pipe(gulp.dest(css.dest))}function script(){return gulp.src(js.src).pipe(concat(js.filename)).pipe(gulp.dest(js.dest)).pipe(uglify()).pipe(rename({extname:".min.js"})).pipe(gulp.dest(js.dest))}function watch(){gulp.watch(css.src,style),gulp.watch(js.src,script)}exports.css=style,exports.js=script,exports.default=watch;